mapper.select:
SELECT t1.hospital_nm , t1.hospital_id , t1.hospital_pwd , t1.hospital_type , t1.hospital_tel_no , t1.mgr_tel_no , t1.hospital_addr , t1.hospital_img , t1.hospital_desc , t1.pwd_mdfy_dt , t2.cd_val AS use_yn_nm , t1.gen_by , t1.gen_dt , t1.mdfy_by , t1.mdfy_dt , t1.new_yn , t3.cd_val AS new_yn_nm FROM t_hospital_info t1 INNER JOIN t_cm_cd t2 ON t1.use_yn = t2.cd AND t2.cat_cd = 'HOSPITAL_USE_YN' INNER JOIN t_cm_cd t3 ON t1.new_yn = t3.cd AND t3.cat_cd = 'HOSPITAL_NEW_YN' WHERE 1=1
AND t1.hospital_nm ILIKE CONCAT('%', #{hospital_nm}, '%')
AND t1.hospital_id ILIKE CONCAT('%', #{hospital_id}, '%')
ORDER BY t1.${order_type} ${order_type2}
ORDER BY t1.GEN_DT DESC
LIMIT ${raw_cnt} OFFSET (${page_cnt} - 1) * ${raw_cnt};

SELECT COUNT(*)
  FROM t_hospital_info
 WHERE hospital_id = #{hospital_id}

SELECT COUNT(*)
  FROM t_hospital_clinic_mapp
 WHERE 1=1
   AND hospital_id = #{hospital_id}
   AND day_of_week = #{day_of_week}

mapper.insert:
INSERT INTO t_hospital_clinic_mapp (
	hospital_id
	, day_of_week

	, clinic_yn


	, c_strt_tm


	, c_end_tm


	, lunch_yn


	, l_strt_tm


	, l_end_tm

) VALUES (
	#{hospital_id}
	, #{day_of_week}

	, #{clinic_yn}


	, #{c_strt_tm}


	, #{c_end_tm}


	, #{lunch_yn}


	, #{l_strt_tm}


	, #{l_end_tm}

)

mapper.update:
	UPDATE t_hospital_clinic_mapp
	   SET hospital_id = #{hospital_id}
		 , day_of_week = #{day_of_week}

	
		
			 , clinic_yn = #{clinic_yn}
		
		
			 , c_strt_tm = #{c_strt_tm}
		
		
			 , c_end_tm = #{c_end_tm}
		
	
	
		
		 , clinic_yn = #{clinic_yn}
		
			 , c_strt_tm = null
			 , c_end_tm = null
	
	 

	
		
			 , lunch_yn = #{lunch_yn}
		
		
			 , l_strt_tm = #{l_strt_tm}
		
		
			 , l_end_tm = #{l_end_tm}
		
	
	
		
			 , lunch_yn = #{lunch_yn}
		
			 , l_strt_tm = #{l_strt_tm}
			 , l_end_tm = #{l_end_tm}
	

	 WHERE 1=1
	   AND hospital_id = #{hospital_id}
	   AND day_of_week = #{day_of_week}

UPDATE t_hospital_info
   SET hospital_id = #{hospital_id}

   	 , hospital_nm = #{hospital_nm}

 
	 , hospital_pwd = #{hospital_pwd}

 
	 , hospital_type = #{hospital_type}

 
	 , hospital_tel_no = #{hospital_tel_no}

 
	 , mgr_tel_no = #{mgr_tel_no}

 
	 , hospital_addr = #{hospital_addr}

 
	 , hospital_img = #{img_file_byte}

 
	 , hospital_desc = #{hospital_desc}

 
	 , pwd_mdfy_dt = now()


	 , use_yn = 'Y'::bpchar

	 , mdfy_by = 'admin'
	 , mdfy_dt = now()

	 , new_yn = #{new_yn}

WHERE 1=1
  AND hospital_id = #{hospital_id}
