mapper.select:
SELECT t1.user_id , t1.user_nm , t1.user_pwd , t1.user_type , t1.user_email , t1.mobile_tel_no , t1.user_desc , t1.user_position , t1.pwd_mdfy_dt , t1.use_yn , t2.cd_val AS use_yn_nm , t1.gen_by , t1.gen_dt , t1.mdfy_by , t1.mdfy_dt , t1.user_code , t1.user_addr FROM t_user_info t1 INNER JOIN t_cm_cd t2 ON t1.use_yn = t2.cd AND t2.cat_cd = 'USER_USE_YN' WHERE 1=1
AND t1.user_nm ILIKE CONCAT('%', #{user_nm}, '%')
AND t1.user_code ILIKE CONCAT('%', #{user_code}, '%')
ORDER BY t1.${order_type} ${order_type2}
ORDER BY t1.GEN_DT DESC
LIMIT ${raw_cnt} OFFSET (${page_cnt} - 1) * ${raw_cnt};

SELECT COUNT(*)
  FROM t_user_info
 WHERE user_code = #{user_code}

mapper.update:
UPDATE t_user_info
   SET mdfy_by = 'admin'
	 , mdfy_dt = now()

	 , user_nm = #{user_nm}


	 , mdfy_user_code = #{mdfy_user_code}


	 , user_addr = #{user_addr}


	 , user_img = #{img_file_byte}

 WHERE user_code = #{user_code}

mapper.insert:
INSERT INTO t_user_family_mapp (
	o_user_code
	, f_user_code

	, f_relation

) VALUES (
	#{user_code}
	, #{family_user_code}

	, #{family_relation}

)

mapper.update:
UPDATE t_user_family_mapp
   SET o_user_code = #{user_code}
	 , f_user_code = #{family_user_code}
	 , f_relation = #{family_relation}
 WHERE 1=1
   AND seq = ${family_seq}

mapper.delete:
UPDATE t_user_info 
   SET use_yn = 'N'
 WHERE user_code = #{user_code}
